import styled from "styled-components";
import { Form } from 'antd';

export const Background = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #191816;
  height: 100vh;
  font-family: 'Poppins';
  @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300&family=Roboto:wght@400;700&display=swap');

  img.SideImg {
    position: absolute;
    width: 720px;
    height: 900px;
    left: 800px;
    top: 0px;
  }
`;

export const LoginBox = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #24221F;
  height: 510px;
  width: 572px;
  color: #AFB6AF;
`

export const Container = styled.div`
  position: absolute;
  width: 334px;
  height: 64px;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;

export const FormStyled = styled(Form)`
   label{
      color: #AFB6AF;
    }
    .ant-form-item-label > label.ant-form-item-required:not(.ant-form-item-required-mark-optional)::before{
      color: #AFB6AF;
    }

  .ant-form-item{
    display: flex;
    flex-direction: column;
    align-items: flex-start;
  }

Input {
  width: 334px;
  height: 35px;
}

Button {
  width: 100%;
  background-color: #FFC632;
  color: #24221F;
  border: #FFC632;
}

.ant-form-item-control-input-content {
  display: flex;
  justify-content: space-between;
  color: #FFC632;
}
`
